    private void btnIngresarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnIngresarActionPerformed
        Usuario usuario = new Usuario();
        usuario.setNombreUsuario(txtUsu.getText());
        usuario.setPassword(txtPass.getText());

        DaoUsuario dao = new DaoUsuario();

        boolean resp = dao.validarusuaro(usuario);

        if (resp) {
            Menu me = new Menu();
            home ho = new home();
            me.setVisible(true);
            ho.setVisible(false);
            
            JOptionPane.showMessageDialog(null, "Bienvenido " + usuario.getNombreUsuario());
            this.setVisible(false);

        } else {

            JOptionPane.showMessageDialog(null, "Usuario no registrado o clave incorrecta ");
            home ho = new home();
            ho.setVisible(true);
            this.setVisible(false);
        }


    }//GEN-LAST:event_btnIngresarActionPerformed

